--
-- Table structure for table `qcm_questions`
--

DROP TABLE IF EXISTS `qcm_questions`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `qcm_questions` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `correct_answer` varchar(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `created_at` datetime(6) DEFAULT NULL,
  `explanation` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `explanation_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `options` json NOT NULL,
  `question` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `question_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `updated_at` datetime(6) DEFAULT NULL,
  `chapter_id` bigint NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_qcm_chapter_id` (`chapter_id`),
  CONSTRAINT `FKh8o4ei6gnycy3l8mw5adjndax` FOREIGN KEY (`chapter_id`) REFERENCES `chapters` (`id`),
  CONSTRAINT `qcm_questions_chk_1` CHECK (json_valid(`options`))
) ENGINE=InnoDB AUTO_INCREMENT=258 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

