--
-- Table structure for table `questions`
--

DROP TABLE IF EXISTS `questions`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `questions` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `exam_id` bigint NOT NULL,
  `type` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `question` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `question_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `instruction` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `instruction_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `points` int NOT NULL,
  `question_order` int DEFAULT NULL,
  `options` json DEFAULT NULL,
  `sub_questions` json DEFAULT NULL,
  `matching_pairs` json DEFAULT NULL,
  `table_content` json DEFAULT NULL,
  `drag_drop_words` json DEFAULT NULL,
  `progressive_phrases` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `criteria` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `helper` json DEFAULT NULL,
  `answer` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `answer_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `sub_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `sub_title_arabic` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `prompt` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `prompt_arabic` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_exam_id` (`exam_id`),
  KEY `idx_exam_order` (`exam_id`,`question_order`),
  KEY `idx_type` (`type`),
  KEY `idx_created_at` (`created_at`),
  CONSTRAINT `questions_ibfk_1` FOREIGN KEY (`exam_id`) REFERENCES `exams` (`id`) ON DELETE CASCADE,
  CONSTRAINT `questions_chk_1` CHECK (json_valid(`options`)),
  CONSTRAINT `questions_chk_2` CHECK (json_valid(`sub_questions`)),
  CONSTRAINT `questions_chk_3` CHECK (json_valid(`matching_pairs`)),
  CONSTRAINT `questions_chk_4` CHECK (json_valid(`table_content`)),
  CONSTRAINT `questions_chk_5` CHECK (json_valid(`drag_drop_words`)),
  CONSTRAINT `questions_chk_6` CHECK (json_valid(`progressive_phrases`)),
  CONSTRAINT `questions_chk_7` CHECK (json_valid(`criteria`)),
  CONSTRAINT `questions_chk_8` CHECK (json_valid(`helper`))
) ENGINE=InnoDB AUTO_INCREMENT=167 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

