--
-- Table structure for table `questions`
--

CREATE TABLE `questions` (
  `id` bigint(20) NOT NULL,
  `exam_id` bigint(20) NOT NULL,
  `type` varchar(255) NOT NULL,
  `question` text NOT NULL,
  `question_arabic` text DEFAULT NULL,
  `instruction` text DEFAULT NULL,
  `instruction_arabic` text DEFAULT NULL,
  `points` int(11) NOT NULL,
  `question_order` int(11) DEFAULT NULL,
  `options` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`options`)),
  `sub_questions` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`sub_questions`)),
  `matching_pairs` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`matching_pairs`)),
  `table_content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`table_content`)),
  `drag_drop_words` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`drag_drop_words`)),
  `progressive_phrases` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`progressive_phrases`)),
  `criteria` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`criteria`)),
  `helper` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`helper`)),
  `answer` text DEFAULT NULL,
  `answer_arabic` text DEFAULT NULL,
  `sub_title` varchar(255) DEFAULT NULL,
  `sub_title_arabic` varchar(255) DEFAULT NULL,
  `prompt` text DEFAULT NULL,
  `prompt_arabic` text DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

