--

INSERT INTO `questions` (`id`, `exam_id`, `type`, `question`, `question_arabic`, `instruction`, `instruction_arabic`, `points`, `question_order`, `options`, `sub_questions`, `matching_pairs`, `table_content`, `drag_drop_words`, `progressive_phrases`, `criteria`, `helper`, `answer`, `answer_arabic`, `sub_title`, `sub_title_arabic`, `prompt`, `prompt_arabic`, `created_at`, `updated_at`) VALUES
(2, 3, 'text', 'hi', 'hh', '', '', 1, 2, '[]', '[]', '[]', NULL, NULL, '[]', NULL, '{\"french\":[],\"arabic\":[]}', 'hh', 'cc', '', '', '', '', '2025-09-28 15:39:09', '2025-09-28 15:39:09'),
(3, 3, 'multiple_choice', 'la capital de france', 'عاصمة فرنسا', '', '', 1, 3, '[]', '[{\"id\":1759182468439,\"question\":\"madrid\",\"questionArabic\":\"مدريد\",\"answer\":\"FAUX\",\"answerArabic\":\"خطأ\"},{\"id\":1759182520780,\"question\":\"paris\",\"questionArabic\":\"باريس\",\"answer\":\"VRAI\",\"answerArabic\":\"صحيح\"}]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '', '', NULL, NULL, NULL, NULL, '2025-09-29 21:49:09', '2025-09-29 21:49:09'),
(4, 3, 'table', 'completer le tableau', 'اكمل الجدول\nLa Boite a mervielles', '', '', 1, 4, '[]', '[]', '[]', '{\"headers\":[\"Roman\",\"ؤ\",\"ف\",\"f\"],\"headersArabic\":[\"الرواية\",\"ي\",\"r\",\"f\"],\"answer\":[\" La Boite a mervielles\",\"ي\",\"r\",\"g\"]}', NULL, NULL, NULL, '{\"french\":[\"Text\"],\"arabic\":[\"نص\"]}', '', '', NULL, NULL, NULL, NULL, '2025-09-30 14:29:51', '2025-09-30 14:31:06'),
(5, 5, 'multiple_choice_single', 'Choisissez la bonne réponse parmi les propositions suivantes :', 'اختر الإجابة الصحيحة من بين الخيارات التالية:\n', '', '', 1, 1, '[]', '[{\"id\":1759704587095,\"question\":\"Titre de l\'œuvre dont le texte est tiré\",\"questionArabic\":\" عنوان العمل الذي أخذ منه النص\",\"options\":[{\"id\":\"a\",\"text\":\"Le chapelet d\'ambre\",\"textArabic\":\"\"},{\"id\":\"b\",\"text\":\"La boîte à merveilles\",\"textArabic\":\"\"}],\"answer\":\"b\"},{\"id\":1759704690596,\"question\":\"Auteur\",\"questionArabic\":\"المؤلف\",\"options\":[{\"id\":\"a\",\"text\":\"Ahmed Sefrioui\",\"textArabic\":\"\"},{\"id\":\"b\",\"text\":\"Victor Hugo\",\"textArabic\":\"\"}],\"answer\":\"a\"},{\"id\":1759704754612,\"question\":\"Son genre littéraire\",\"questionArabic\":\"نوعه الأدبي\",\"options\":[{\"id\":\"a\",\"text\":\"Pièce de théâtre\",\"textArabic\":\"\"},{\"id\":\"b\",\"text\":\"Roman autobiographique\",\"textArabic\":\"\"}],\"answer\":\"b\"},{\"id\":1759704806767,\"question\":\"Sa date de publication\",\"questionArabic\":\"تاريخ نشره\",\"options\":[{\"id\":\"a\",\"text\":\"1960\",\"textArabic\":\"\"},{\"id\":\"b\",\"text\":\"1954\",\"textArabic\":\"\"}],\"answer\":\"b\"}]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '', '', NULL, NULL, NULL, NULL, '2025-10-02 16:22:05', '2025-10-05 22:54:37'),
(6, 4, 'table', 'Recopiez et complétez le tableau suivant', 'انسخ وأكمل الجدول التالي\n', '', '', 1, 1, '[]', '[]', '[]', '{\"headers\":[\"Titre\",\"Auteur\",\"Genre\",\"Siècle\"],\"headersArabic\":[\"العنوان\",\"الكاتب\",\"النوع\",\"القرن\"],\"answer\":[\"La Boîte à Merveilles\",\"Ahmed Sefrioui\",\"Roman autobiographique\",\"20ème siècle\"]}', NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '', '', NULL, NULL, NULL, NULL, '2025-10-02 17:42:02', '2025-10-02 17:50:25'),
(7, 4, 'multiple_choice_single', 'D\'après votre lecture de l\'œuvre, ce texte se situe après : ', 'حسب قراءتك للعمل، يقع هذا النص بعد:', '', '', 1, 2, '[{\"id\":\"1\",\"text\":\"la visite de Sidi Ali Boughaleb\",\"textArabic\":\"\"},{\"id\":\"2\",\"text\":\"le retour du père de son voyage\",\"textArabic\":\"\"},{\"id\":\"3\",\"text\":\"la dispute du père avec le dellal au souk des bijoux\",\"textArabic\":\"\"}]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '1', '', NULL, NULL, NULL, NULL, '2025-10-02 17:55:58', '2025-10-02 17:55:58'),
(8, 4, 'text', ' Pourquoi Sidi Mohamed hésita-t-il à parler à Rahma ?', ' لماذا تردد سيدي محمد في التحدث إلى رحمة؟', '', '', 1, 3, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Sidi Mohamed hésita parce que sa mère ne parlait plus à Rahma depuis leur dispute. Les deux femmes évitaient de se rencontrer, alors l\'enfant ne savait pas s\'il fallait lui sourire ou se sauver.', ' تردد سيدي محمد لأن أمه لم تعد تتحدث إلى رحمة منذ خلافهما. كانت المرأتان تتجنبان لقاء بعضهما البعض، لذلك لم يكن الطفل يعرف إن كان عليه أن يبتسم لها أو يهرب.', NULL, NULL, NULL, NULL, '2025-10-02 18:45:32', '2025-10-02 18:45:32'),
(9, 4, 'text', 'Rahma montait l\'escalier quand Sidi Mohammed descendait. Que lui donna-t-elle ?', 'كانت رحمة تصعد السلم بينما كان سيدي محمد ينزل. ماذا أعطته؟', '', '', 1, 4, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Rahma lui donna un gros cabochon de verre à facettes taillé en diamant (un objet lisse et froid / un bout de verre).', 'أعطته رحمة قطعة زجاج كبيرة مصقولة على شكل ماسة (شيء أملس وبارد / قطعة زجاج).', NULL, NULL, NULL, NULL, '2025-10-02 18:49:21', '2025-10-02 18:53:31'),
(10, 4, 'text', 'Relevez du texte la phrase qui montre le jugement dévalorisant de la mère à propos de cet objet offert par Rahma ?', 'استخرج من النص الجملة التي تظهر حكم الأم المحتقر لهذا الشيء الذي قدمته رحمة؟', '', '', 1, 5, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '\"Encore un bout de verre ! Fais attention de ne pas te blesser.\"', '\"قطعة زجاج أخرى! انتبه حتى لا تجرح نفسك.\"', NULL, NULL, NULL, NULL, '2025-10-02 18:55:14', '2025-10-02 18:55:14'),
(11, 4, 'text', 'Relevez du texte quatre mots relatifs au monde merveilleux.', 'استخرج من النص أربع كلمات تتعلق بالعالم العجائبي', '', '', 1, 6, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '1. fabuleux\n2. palais souterrain\n3. puissances de l\'Invisible\n4. talisman\n5. démons\n6. Boîte à Merveilles\n7. vertus\n(Accepter 4 parmi ces mots)', '1. خرافي\n2. قصر تحت الأرض\n3. قوى الغيب\n4. تعويذة\n5. شياطين\n6. صندوق العجائب\n7. فضائل\n(يقبل 4 من هذه الكلمات)', NULL, NULL, NULL, NULL, '2025-10-02 19:06:54', '2025-10-02 19:06:54'),
(12, 4, 'text', '[- C\'est pour toi, me murmura notre voisine.] Transformez la phrase ci-dessus au discours indirect (avec verbe introducteur au passé simple).', ' [- هذا لك، همست جارتنا.] حول الجملة أعلاه إلى أسلوب غير مباشر (مع فعل تمهيدي في الماضي البسيط)', '', '', 1, 7, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Notre voisine me murmura que c\'était pour moi.', 'همست جارتنا لي أن ذلك كان من أجلي.', NULL, NULL, NULL, NULL, '2025-10-02 19:21:14', '2025-10-02 19:21:14'),
(13, 4, 'multiple_choice_single', ' Identifiez la figure de style dans la phrase suivante : « Je tenais dans mes mains un objet d\'une richesse insoupçonnable. »', ' حدد الصورة البلاغية في الجملة التالية: « كنت أمسك بين يدي شيئا ذا غنى لا يمكن تصوره. » انسخ الإجابة الصحيحة', '', '', 1, 8, '[{\"id\":\"a\",\"text\":\"comparaison\",\"textArabic\":\"\"},{\"id\":\"b\",\"text\":\"métaphore\",\"textArabic\":\"\"},{\"id\":\"c\",\"text\":\"hyperbole\",\"textArabic\":\"\"}]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'c', '', NULL, NULL, NULL, NULL, '2025-10-02 19:22:49', '2025-10-02 19:22:49'),
(14, 4, 'word_placement', 'Comment trouvez-vous le comportement de Rahma vis-à-vis de Sidi Mohamed ? Justifiez votre opinion.', 'كيف تجد تصرف رحمة تجاه سيدي محمد؟ برر رأيك.', '', '', 1, 9, '[]', '[]', '[]', NULL, '{\"template\":\"Le [0] de Rahma est [1] et [2]. Malgré le [3] avec la mère de Sidi Mohamed, elle ne mêle pas l\'enfant à cette [4] et lui offre un cadeau qui le rend heureux. Cela montre sa [5] et son [6] pour l\'enfant, indépendamment des problèmes entre adultes. \",\"words\":[\"comportement\",\"généreux\",\"gentil\",\"conflit\",\"dispute\",\"bienveillance\",\"affection\"]}', NULL, NULL, '{\"french\":[\"généreux\",\"gentil\",\"conflit\",\"dispute\",\"bienveillance\",\"affection\",\"comportement\"],\"arabic\":[\"كريم\",\"لطيف\",\"خلاف\",\"نزاع\",\"لطف\",\"محبة\",\"تصرف\"]}', 'Le comportement de Rahma est généreux et gentil. Malgré le conflit avec la mère de Sidi Mohamed, elle ne mêle pas l\'enfant à cette dispute et lui offre un cadeau qui le rend heureux. Cela montre sa bienveillance et son affection pour l\'enfant, indépendamment des problèmes entre adultes.', 'تصرف رحمة كريم ولطيف. رغم الخلاف مع والدة سيدي محمد، فهي لا تُدخل الطفل في هذا النزاع وتقدم له هدية تجعله سعيدًا. هذا يُظهر لطفها ومحبتها للطفل، بغض النظر عن المشاكل بين الكبار.\n', NULL, NULL, NULL, NULL, '2025-10-02 19:48:11', '2025-10-05 00:37:44'),
(15, 4, 'word_placement', 'Selon vous, un jouet peut-il nous éloigner de la solitude ? Justifiez votre opinion.', 'في رأيك، هل يمكن للعبة أن تبعدنا عن الوحدة؟ برر رأيك.', '', '', 1, 10, '[]', '[]', '[]', NULL, '{\"template\":\"Un [0] peut éloigner de la [1] car il stimule l\'[2], permet de créer des [3] et des univers, et devient un [4] pour l\'enfant [5], comme la Boîte à Merveilles pour Sidi Mohamed.\\n\",\"words\":[\"jouet\",\"solitude\",\"imagination\",\"histoires\",\"compagnon\",\"solitaire\"]}', NULL, NULL, '{\"french\":[\"jouet\",\"solitude\",\"imagination\",\"histoires\",\"compagnon\",\"solitaire\"],\"arabic\":[\"لعبة\",\"وحدة\",\"خيال\",\"قصص\",\"رفيق\",\"وحيد\"]}', 'Un jouet peut éloigner de la solitude car il stimule l\'imagination, permet de créer des histoires et des univers, et devient un compagnon pour l\'enfant solitaire, comme la Boîte à Merveilles pour Sidi Mohamed.\n', 'يمكن للعبة أن تبعد الوحدة لأنها تحفز الخيال، وتسمح بخلق القصص والعوالم، وتصبح رفيقًا للطفل الوحيد، مثل صندوق العجائب لسيدي محمد.\n', NULL, NULL, NULL, NULL, '2025-10-02 23:01:17', '2025-10-02 23:01:17'),
(16, 5, 'text', 'Situez ce passage dans l\'œuvre et dites si c\'est avant ou après la célébration de la ACHOURA ?', 'حدد موقع هذا المقطع في العمل وقل إذا كان قبل أو بعد احتفال عاشوراء؟', '', '', 1, 2, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Ce passage se situe avant la célébration de l\'Achoura', 'يقع هذا المقطع قبل احتفال عاشوراء', NULL, NULL, NULL, NULL, '2025-10-05 22:57:16', '2025-10-05 22:57:16'),
(17, 5, 'text', 'Quel métier exerce Si Abderrahman ?', 'ما هي المهنة التي يمارسها السي عبد الرحمان؟', '', '', 1, 3, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Si Abderrahman exerce le métier de barbier (coiffeur)', 'السي عبد الرحمان يمارس مهنة الحلاق', NULL, NULL, NULL, NULL, '2025-10-05 22:57:53', '2025-10-05 22:57:53'),
(18, 5, 'text', '« Il ajouta profitant du silence forcé du barbier », à travers cette phrase le narrateur voulait-il dire que Si Abderrahman : aime le silence ou bien aime bavarder ? Justifiez votre réponse.', '«أضاف مستفيداً من الصمت القسري للحلاق»، من خلال هذه العبارة هل أراد الراوي أن يقول إن السي عبد الرحمان: يحب الصمت أم يحب الثرثرة؟ برر إجابتك.', '', '', 1, 4, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Le narrateur veut dire que Si Abderrahman aime bavarder. La phrase indique qu\'il profite du moment où le barbier est forcé au silence (car il travaille) pour parler librement sans être interrompu.', 'يريد الراوي أن يقول إن السي عبد الرحمان يحب الثرثرة. تشير العبارة إلى أنه يستغل اللحظة التي يكون فيها الحلاق مجبراً على الصمت (لأنه يعمل) ليتحدث بحرية دون أن يقاطعه أحد.', NULL, NULL, NULL, NULL, '2025-10-05 23:00:23', '2025-10-05 23:00:23'),
(19, 5, 'multiple_choice_single', 'D\'après le texte, Sidi Ahmed n\'est pas bien renseigné sur son propre mariage. Dites pourquoi en choisissant parmi les arguments suivants', 'حسب النص، سيدي أحمد ليس على علم جيد بزواجه. قل لماذا باختيار من بين الحجج التالية', '', '', 1, 5, '[{\"id\":\"a\",\"text\":\"Il est indifférent à son mariage\",\"textArabic\":\"هو غير مبال بزواجه\"},{\"id\":\"b\",\"text\":\"Les parents qui s\'occupent de cette affaire \",\"textArabic\":\"الآباء الذين يهتمون بهذا الأمر\"},{\"id\":\"c\",\"text\":\"Il n\'a pas son mot à dire \",\"textArabic\":\"ليس له رأي في الأمر\"}]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'c', '', NULL, NULL, NULL, NULL, '2025-10-05 23:03:02', '2025-10-05 23:03:02'),
(20, 5, 'multiple_choice_single', 'Pourquoi les jeunes gens ne doivent pas avoir leur mot à dire à propos du mariage, selon l\'un des personnages du texte ? Choisissez la bonne réponse parmi ces propositions', 'لماذا لا ينبغي للشباب أن يكون لهم رأي في موضوع الزواج، حسب إحدى الشخصيات في النص؟ اختر الإجابة الصحيحة من بين هذه الاقتراحات', '', '', 1, 6, '[{\"id\":\"a\",\"text\":\"Parce qu\'ils ne sont pas instruits\",\"textArabic\":\"لأنهم غير متعلمين\"},{\"id\":\"b\",\"text\":\"Parce qu\'il leur manque l\'expérience des gens mûrs, des points de comparaison, la connaissance des hommes\",\"textArabic\":\"لأنهم يفتقرون إلى خبرة الناس الناضجين، ونقاط المقارنة، ومعرفة البشر\"},{\"id\":\"c\",\"text\":\"Parce qu\'ils sont sentimentaux et impulsifs\",\"textArabic\":\"لأنهم عاطفيون ومتهورون\"}]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'b', '', NULL, NULL, NULL, NULL, '2025-10-05 23:05:12', '2025-10-05 23:05:12'),
(21, 5, 'text', '« La rue qui rêve, qui chante et qui boude» de quelle figure de style s\'agit-il ? Hyperbole ? Métaphore ? Personnification ? Quel est son effet ?', '«الشارع الذي يحلم، ويغني ويعبس» ما هي الصورة البلاغية المستخدمة؟ مبالغة؟ استعارة؟ تشخيص؟ ما هو تأثيرها؟', '', '', 1, 7, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'Il s\'agit d\'une personnification. Son effet est de donner vie à la rue en lui attribuant des caractéristiques humaines (rêver, chanter, bouder), ce qui rend la description plus vivante et poétique.', 'إنه تشخيص. تأثيره هو إعطاء الحياة للشارع من خلال نسب خصائص إنسانية إليه (الحلم، الغناء، العبوس)، مما يجعل الوصف أكثر حيوية وشاعرية.', NULL, NULL, NULL, NULL, '2025-10-05 23:06:45', '2025-10-05 23:06:45'),
(22, 5, 'text', 'Quelle est la tonalité dominante dans le dernier paragraphe du texte ? Ironique ? Pathétique ? Lyrique ? Justifiez votre réponse.', 'ما هي النبرة السائدة في الفقرة الأخيرة من النص؟ ساخرة؟ مؤثرة؟ غنائية؟ برر إجابتك.', '', '', 1, 8, '[]', '[]', '[]', NULL, NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', 'La tonalité est lyrique. Le narrateur exprime ses sentiments avec poésie en décrivant la rue de manière émotionnelle et imagée, utilisant des figures de style pour transmettre son attachement et sa vision personnelle du lieu.', ' النبرة غنائية. يعبر الراوي عن مشاعره بشاعرية من خلال وصف الشارع بطريقة عاطفية ومجازية، مستخدماً الصور البلاغية لنقل ارتباطه ورؤيته الشخصية للمكان.', NULL, NULL, NULL, NULL, '2025-10-05 23:07:39', '2025-10-05 23:07:39'),
(24, 3, 'table', 'Recopiez et complétez le tableau suivant:', 'انسخ وأكمل الجدول التالي:', '', '', 1, 5, '[]', '[]', '[]', '{\"headers\":[\"Titre de l\'œuvre\",\"L\'auteur\",\"Genre littéraire\",\"Date de publication\"],\"headersArabic\":[\"عنوان العمل\",\"المؤلف\",\"النوع الأدبي\",\"تاريخ النشر\"],\"answer\":[\"La Boîte à Merveilles\",\"Ahmed Sefrioui\",\"Roman autobiographique\",\"1954\"],\"answerArabic\":[\"صندوق العجائب\",\"أحمد الصفريوي\",\"رواية سيرة ذاتية\",\"1954\"]}', NULL, NULL, NULL, '{\"french\":[],\"arabic\":[]}', '', '', NULL, NULL, NULL, NULL, '2025-10-09 13:13:16', '2025-10-09 13:13:16');

