INSERT INTO `qcm_questions` VALUES (3,'a','2025-10-07 23:31:32.000000','','','[{\"id\": \"a\", \"text\": \"Un adulte qui se souvient de son enfance\", \"textArabic\": \"شخص بالغ يتذكر طفولته\"}, {\"id\": \"b\", \"text\": \"Un enfant de dix ans\", \"textArabic\": \"طفل في العاشرة\"}, {\"id\": \"c\", \"text\": \"Le père Abdeslam\", \"textArabic\": \"الأب عبد السلام\"}, {\"id\": \"d\", \"text\": \"Lalla Zoubida\", \"textArabic\": \"لالة زبيدة\"}]','Qui est le narrateur de l\'histoire?','من هو راوي القصة؟','2025-10-07 23:31:32.000000',1),(4,'b','2025-10-07 23:32:12.000000','','','[{\"id\": \"a\", \"text\": \"Mohammed\", \"textArabic\": \"محمد\"}, {\"id\": \"b\", \"text\": \"Sidi Mohamed\", \"textArabic\": \"سيدي محمد\"}, {\"id\": \"c\", \"text\": \"Abdeslam\", \"textArabic\": \"عبد السلام\"}, {\"id\": \"d\", \"text\": \"Driss\", \"textArabic\": \"إدريس\"}]','Comment s\'appelle le narrateur enfant?','ما اسم الراوي الطفل؟','2025-10-07 23:32:12.000000',1),(5,'c','2025-10-07 23:33:10.000000','','','[{\"id\": \"a\", \"text\": \"La joie\", \"textArabic\": \"الفرح\"}, {\"id\": \"b\", \"text\": \"La colère\", \"textArabic\": \"الغضب\"}, {\"id\": \"c\", \"text\": \"La solitude\", \"textArabic\": \"الوحدة\"}, {\"id\": \"d\", \"text\": \"L\'indifférence\", \"textArabic\": \"اللامبالاة\"}]','Quel est le principal sentiment du narrateur au début du chapitre?','ما هو الشعور الرئيسي للراوي في بداية الفصل؟','2025-10-07 23:33:10.000000',1),(6,'b','2025-10-07 23:33:33.000000','','','[{\"id\": \"a\", \"text\": \"Lalla Aïcha\", \"textArabic\": \"لالة عائشة\"}, {\"id\": \"b\", \"text\": \"Lalla Zoubida\", \"textArabic\": \"لالة زبيدة\"}, {\"id\": \"c\", \"text\": \"Lalla Fatma\", \"textArabic\": \"لالة فاطمة\"}, {\"id\": \"d\", \"text\": \"Lalla Kenza\", \"textArabic\": \"لالة كنزة\"}]','Comment s\'appelle la mère du narrateur?','ما اسم أم الراوي؟','2025-10-07 23:33:33.000000',1),(7,'b','2025-10-07 23:33:55.000000','','','[{\"id\": \"a\", \"text\": \"Menuisier\", \"textArabic\": \"نجار\"}, {\"id\": \"b\", \"text\": \"Tisserand\", \"textArabic\": \"حائك\"}, {\"id\": \"c\", \"text\": \"Commerçant\", \"textArabic\": \"تاجر\"}, {\"id\": \"d\", \"text\": \"Forgeron\", \"textArabic\": \"حداد\"}]','Quelle est la profession du père?','ما هي مهنة الأب؟','2025-10-07 23:33:55.000000',1),(8,'c','2025-10-07 23:34:16.000000','','','[{\"id\": \"a\", \"text\": \"Casablanca\", \"textArabic\": \"الدار البيضاء\"}, {\"id\": \"b\", \"text\": \"Rabat\", \"textArabic\": \"الرباط\"}, {\"id\": \"c\", \"text\": \"Fès\", \"textArabic\": \"فاس\"}, {\"id\": \"d\", \"text\": \"Marrakech\", \"textArabic\": \"مراكش\"}]','Dans quelle ville se déroule l\'histoire?','في أي مدينة تدور أحداث القصة؟','2025-10-07 23:34:16.000000',1),(9,'b','2025-10-07 23:34:50.000000','','','[{\"id\": \"a\", \"text\": \"Une boîte contenant des bijoux\", \"textArabic\": \"صندوق يحتوي على مجوهرات\"}, {\"id\": \"b\", \"text\": \"Une boîte contenant les jouets de l\'enfant\", \"textArabic\": \"صندوق يحتوي على ألعاب الطفل\"}, {\"id\": \"c\", \"text\": \"Une boîte magique\", \"textArabic\": \"صندوق سحري\"}, {\"id\": \"d\", \"text\": \"Une boîte de vêtements\", \"textArabic\": \"صندوق ملابس\"}]','Qu\'est-ce que la boîte à merveilles?','ما هو صندوق العجائب؟','2025-10-07 23:34:50.000000',1),(10,'c','2025-10-07 23:35:10.000000','','','[{\"id\": \"a\", \"text\": \"Très riche\", \"textArabic\": \"غنية جدا\"}, {\"id\": \"b\", \"text\": \"Pauvre\", \"textArabic\": \"فقيرة\"}, {\"id\": \"c\", \"text\": \"Modeste\", \"textArabic\": \"متواضعة\"}, {\"id\": \"d\", \"text\": \"Aristocratique\", \"textArabic\": \"أرستقراطية\"}]','Quel est le statut social de la famille?','ما هي الحالة الاجتماعية للعائلة؟','2025-10-07 23:35:10.000000',1),(11,'c','2025-10-07 23:35:33.000000','','','[{\"id\": \"a\", \"text\": \"Dans la rue avec des amis\", \"textArabic\": \"في الشارع مع الأصدقاء\"}, {\"id\": \"b\", \"text\": \"À la mosquée\", \"textArabic\": \"في المسجد\"}, {\"id\": \"c\", \"text\": \"Seul à la maison\", \"textArabic\": \"وحيدا في المنزل\"}, {\"id\": \"d\", \"text\": \"Au marché\", \"textArabic\": \"في السوق\"}]','Où l\'enfant passe-t-il la plupart de son temps?','أين يقضي الطفل معظم وقته؟','2025-10-07 23:35:33.000000',1),(12,'b','2025-10-07 23:35:52.000000','','','[{\"id\": \"a\", \"text\": \"En lisant des livres\", \"textArabic\": \"بقراءة الكتب\"}, {\"id\": \"b\", \"text\": \"En jouant avec sa boîte à merveilles\", \"textArabic\": \"باللعب بصندوق العجائب\"}, {\"id\": \"c\", \"text\": \"En dormant\", \"textArabic\": \"بالنوم\"}, {\"id\": \"d\", \"text\": \"En dessinant\", \"textArabic\": \"بالرسم\"}]','Comment l\'enfant occupe-t-il sa solitude?','كيف يشغل الطفل وحدته؟','2025-10-07 23:35:52.000000',1),(13,'b','2025-10-07 23:38:22.000000','','','[{\"id\": \"a\", \"text\": \"4 ans\", \"textArabic\": \"4 سنوات\"}, {\"id\": \"b\", \"text\": \"6 ans\", \"textArabic\": \"6 سنوات\"}, {\"id\": \"c\", \"text\": \"10 ans\", \"textArabic\": \"10 سنوات\"}, {\"id\": \"d\", \"text\": \"12 ans\", \"textArabic\": \"12 سنة\"}]','Quel âge a approximativement le narrateur enfant?','ما هو العمر التقريبي للراوي الطفل؟','2025-10-07 23:38:22.000000',1),(14,'b','2025-10-07 23:38:39.000000','','','[{\"id\": \"a\", \"text\": \"Abdellah\", \"textArabic\": \"عبد الله\"}, {\"id\": \"b\", \"text\": \"Abdeslam\", \"textArabic\": \"عبد السلام\"}, {\"id\": \"c\", \"text\": \"Ahmed\", \"textArabic\": \"أحمد\"}, {\"id\": \"d\", \"text\": \"Abderrahmane\", \"textArabic\": \"عبد الرحمان\"}]','Comment s\'appelle le père du narrateur?','ما اسم والد الراوي؟','2025-10-07 23:38:39.000000',1),(15,'c','2025-10-07 23:38:55.000000','','','[{\"id\": \"a\", \"text\": \"À la maison\", \"textArabic\": \"في المنزل\"}, {\"id\": \"b\", \"text\": \"Dans un souk\", \"textArabic\": \"في سوق\"}, {\"id\": \"c\", \"text\": \"Dans un atelier de tissage à Derb el Aïssa\", \"textArabic\": \"في ورشة حياكة في درب العيسى\"}, {\"id\": \"d\", \"text\": \"Dans une mosquée\", \"textArabic\": \"في مسجد\"}]','Où travaille le père?','أين يعمل الأب؟','2025-10-07 23:38:55.000000',1),(16,'b','2025-10-07 23:39:16.000000','','','[{\"id\": \"a\", \"text\": \"Elle l\'ignore complètement\", \"textArabic\": \"تتجاهله تماما\"}, {\"id\": \"b\", \"text\": \"Elle est inquiète et attentive\", \"textArabic\": \"قلقة ومهتمة\"}, {\"id\": \"c\", \"text\": \"Elle le punit\", \"textArabic\": \"تعاقبه\"}, {\"id\": \"d\", \"text\": \"Elle lui trouve des amis\", \"textArabic\": \"تجد له أصدقاء\"}]','Quelle est la réaction de la mère face à la solitude de son fils?','ما هو رد فعل الأم تجاه وحدة ابنها؟','2025-10-07 23:39:16.000000',1),(17,'c','2025-10-07 23:39:32.000000','','','[{\"id\": \"a\", \"text\": \"Un quartier moderne\", \"textArabic\": \"حي حديث\"}, {\"id\": \"b\", \"text\": \"Un quartier européen\", \"textArabic\": \"حي أوروبي\"}, {\"id\": \"c\", \"text\": \"La médina (vieille ville)\", \"textArabic\": \"المدينة القديمة\"}, {\"id\": \"d\", \"text\": \"Un quartier rural\", \"textArabic\": \"حي ريفي\"}]','Quel type de quartier habite la famille?','في أي نوع من الأحياء تسكن العائلة؟','2025-10-07 23:39:32.000000',1),(18,'b','2025-10-07 23:39:51.000000','','','[{\"id\": \"a\", \"text\": \"Joyeuse et animée\", \"textArabic\": \"مرحة ومفعمة بالحيوية\"}, {\"id\": \"b\", \"text\": \"Silencieuse et calme\", \"textArabic\": \"هادئة وساكنة\"}, {\"id\": \"c\", \"text\": \"Bruyante et chaotique\", \"textArabic\": \"صاخبة وفوضوية\"}, {\"id\": \"d\", \"text\": \"Hostile\", \"textArabic\": \"عدائية\"}]','Quelle est l\'atmosphère générale de la maison?','ما هو الجو العام للمنزل؟','2025-10-07 23:39:51.000000',1),(19,'b','2025-10-07 23:40:06.000000','','','[{\"id\": \"a\", \"text\": \"Grande et luxueuse\", \"textArabic\": \"كبير وفاخر\"}, {\"id\": \"b\", \"text\": \"Petite et modeste\", \"textArabic\": \"صغير ومتواضع\"}, {\"id\": \"c\", \"text\": \"En ruine\", \"textArabic\": \"متهدم\"}, {\"id\": \"d\", \"text\": \"Moderne et spacieuse\", \"textArabic\": \"حديث وواسع\"}]','Comment est décrite la maison du narrateur?','كيف يوصف منزل الراوي؟','2025-10-07 23:40:06.000000',1),(20,'c','2025-10-07 23:40:21.000000','','','[{\"id\": \"a\", \"text\": \"Un livre\", \"textArabic\": \"كتاب\"}, {\"id\": \"b\", \"text\": \"Une poupée\", \"textArabic\": \"دمية\"}, {\"id\": \"c\", \"text\": \"La boîte à merveilles\", \"textArabic\": \"صندوق العجائب\"}, {\"id\": \"d\", \"text\": \"Un ballon\", \"textArabic\": \"كرة\"}]','Quel objet symbolise l\'imaginaire de l\'enfant?','ما هو الشيء الذي يرمز إلى خيال الطفل؟','2025-10-07 23:40:21.000000',1),(21,'c','2025-10-07 23:40:35.000000','','','[{\"id\": \"a\", \"text\": \"Le commerce\", \"textArabic\": \"التجارة\"}, {\"id\": \"b\", \"text\": \"L\'enseignement\", \"textArabic\": \"التعليم\"}, {\"id\": \"c\", \"text\": \"Les travaux ménagers\", \"textArabic\": \"الأعمال المنزلية\"}, {\"id\": \"d\", \"text\": \"La couture\", \"textArabic\": \"الخياطة\"}]','Quelle est la principale activité de Lalla Zoubida?','ما هو النشاط الرئيسي للالة زبيدة؟','2025-10-07 23:40:35.000000',1),(22,'b','2025-10-07 23:40:50.000000','','','[{\"id\": \"a\", \"text\": \"Avec compréhension\", \"textArabic\": \"بفهم\"}, {\"id\": \"b\", \"text\": \"Avec incompréhension et étrangeté\", \"textArabic\": \"بعدم فهم وغرابة\"}, {\"id\": \"c\", \"text\": \"Avec indifférence\", \"textArabic\": \"بلامبالاة\"}, {\"id\": \"d\", \"text\": \"Avec mépris\", \"textArabic\": \"باحتقار\"}]','Comment l\'enfant perçoit-il le monde adulte?','كيف يدرك الطفل عالم الكبار؟','2025-10-07 23:40:50.000000',1),(23,'b','2025-10-07 23:41:05.000000','','','[{\"id\": \"a\", \"text\": \"Moderne et rapide\", \"textArabic\": \"حديث وسريع\"}, {\"id\": \"b\", \"text\": \"Traditionnel et répétitif\", \"textArabic\": \"تقليدي ومتكرر\"}, {\"id\": \"c\", \"text\": \"Imprévisible\", \"textArabic\": \"لا يمكن التنبؤ به\"}, {\"id\": \"d\", \"text\": \"Nomade\", \"textArabic\": \"بدوي\"}]','Quel est le rythme de vie de la famille?','ما هو نمط حياة العائلة؟','2025-10-07 23:41:05.000000',1),(24,'b','2025-10-07 23:41:19.000000','','','[{\"id\": \"a\", \"text\": \"Une relation distante\", \"textArabic\": \"علاقة باردة\"}, {\"id\": \"b\", \"text\": \"Une relation fusionnelle et affectueuse\", \"textArabic\": \"علاقة قوية وحنونة\"}, {\"id\": \"c\", \"text\": \"Une relation conflictuelle\", \"textArabic\": \"علاقة متضاربة\"}, {\"id\": \"d\", \"text\": \"Aucune relation\", \"textArabic\": \"لا توجد علاقة\"}]','Quelle relation unit l\'enfant et sa mère?','ما هي العلاقة التي تربط الطفل بأمه؟','2025-10-07 23:41:19.000000',1),(25,'b','2025-10-07 23:41:34.000000','','','[{\"id\": \"a\", \"text\": \"Il est méchant\", \"textArabic\": \"إنه شرير\"}, {\"id\": \"b\", \"text\": \"Il est enfant unique et isolé\", \"textArabic\": \"إنه طفل وحيد ومعزول\"}, {\"id\": \"c\", \"text\": \"Ses parents l\'interdisent\", \"textArabic\": \"والداه يمنعانه\"}, {\"id\": \"d\", \"text\": \"Il n\'aime personne\", \"textArabic\": \"لا يحب أحدا\"}]','Pourquoi l\'enfant n\'a-t-il pas d\'amis?','لماذا لا يملك الطفل أصدقاء؟','2025-10-07 23:41:34.000000',1),(26,'b','2025-10-07 23:41:48.000000','','','[{\"id\": \"a\", \"text\": \"La richesse\", \"textArabic\": \"الثروة\"}, {\"id\": \"b\", \"text\": \"Un refuge et un monde imaginaire\", \"textArabic\": \"ملجأ وعالم خيالي\"}, {\"id\": \"c\", \"text\": \"Un cadeau\", \"textArabic\": \"هدية\"}, {\"id\": \"d\", \"text\": \"Un secret\", \"textArabic\": \"سر\"}]','Que symbolise la boîte pour l\'enfant?','ماذا يرمز الصندوق بالنسبة للطفل؟','2025-10-07 23:41:48.000000',1),(27,'a','2025-10-07 23:42:05.000000','','','[{\"id\": \"a\", \"text\": \"La nostalgie\", \"textArabic\": \"الحنين\"}, {\"id\": \"b\", \"text\": \"La colère\", \"textArabic\": \"الغضب\"}, {\"id\": \"c\", \"text\": \"La peur\", \"textArabic\": \"الخوف\"}, {\"id\": \"d\", \"text\": \"L\'excitation\", \"textArabic\": \"الإثارة\"}]','Quel sentiment domine dans le récit du chapitre 1?','ما هو الشعور السائد في سرد الفصل الأول؟','2025-10-07 23:42:05.000000',1),(31,'a','2025-10-14 11:13:40.000000','','','[{\"id\": \"a\", \"text\": \"Au Msid (école coranique)\", \"textArabic\": \"في المسيد (الكُتّاب)\"}, {\"id\": \"b\", \"text\": \"Au souk\", \"textArabic\": \"في السوق\"}, {\"id\": \"c\", \"text\": \"Chez Lalla Aïcha\", \"textArabic\": \"عند لالة عائشة\"}, {\"id\": \"d\", \"text\": \"Au hammam\", \"textArabic\": \"في الحمام\"}]','Où se déroule principalement le chapitre 2 de La Boîte à Merveilles ?','أين تجري أحداث الفصل الثاني من صندوق العجائب بشكل رئيسي؟','2025-10-14 11:13:40.000000',2),(32,'b','2025-10-14 11:13:57.000000','','','[{\"id\": \"a\", \"text\": \"Le père de Sidi Mohammed\", \"textArabic\": \"والد سيدي محمد\"}, {\"id\": \"b\", \"text\": \"Le maître d\'école coranique\", \"textArabic\": \"معلم الكُتّاب\"}, {\"id\": \"c\", \"text\": \"Un vendeur au souk\", \"textArabic\": \"بائع في السوق\"}, {\"id\": \"d\", \"text\": \"Un voisin de la famille\", \"textArabic\": \"جار العائلة\"}]','Qui est le Fqih dans le chapitre 2 ?','من هو الفقيه في الفصل الثاني؟','2025-10-14 11:13:57.000000',2),(33,'b','2025-10-14 11:14:13.000000','','','[{\"id\": \"a\", \"text\": \"Il adore y aller et apprendre\", \"textArabic\": \"يحب الذهاب إليه والتعلم\"}, {\"id\": \"b\", \"text\": \"Il a peur et n\'aime pas cette expérience\", \"textArabic\": \"يخاف ولا يحب هذه التجربة\"}, {\"id\": \"c\", \"text\": \"Il est indifférent\", \"textArabic\": \"لا يبالي\"}, {\"id\": \"d\", \"text\": \"Il y va uniquement pour jouer avec ses amis\", \"textArabic\": \"يذهب فقط للعب مع أصدقائه\"}]','Quelle est l\'attitude de Sidi Mohammed envers le Msid ?','ما هو موقف سيدي محمد تجاه المسيد؟','2025-10-14 11:14:13.000000',2),(34,'a','2025-10-14 11:14:47.000000','','','[{\"id\": \"a\", \"text\": \"Un symbole d\'autorité et de punition\", \"textArabic\": \"رمز للسلطة والعقاب\"}, {\"id\": \"b\", \"text\": \"Un simple bâton de marche\", \"textArabic\": \"مجرد عصا للمشي\"}, {\"id\": \"c\", \"text\": \"Un jouet pour les enfants\", \"textArabic\": \"لعبة للأطفال\"}, {\"id\": \"d\", \"text\": \"Un outil pour écrire\", \"textArabic\": \"أداة للكتابة\"}]','Que représente la \'baguette\' du Fqih dans le chapitre ?','ماذا تمثل عصا الفقيه في الفصل؟','2025-10-14 11:14:47.000000',2),(35,'a','2025-10-14 11:15:02.000000','','','[{\"id\": \"a\", \"text\": \"Par la répétition et la mémorisation orale\", \"textArabic\": \"بالتكرار والحفظ الشفهي\"}, {\"id\": \"b\", \"text\": \"En lisant des livres modernes\", \"textArabic\": \"بقراءة الكتب الحديثة\"}, {\"id\": \"c\", \"text\": \"Par des jeux éducatifs\", \"textArabic\": \"من خلال الألعاب التعليمية\"}, {\"id\": \"d\", \"text\": \"En écoutant des enregistrements\", \"textArabic\": \"بالاستماع إلى التسجيلات\"}]','Comment Sidi Mohammed apprend-il le Coran au Msid ?','كيف يتعلم سيدي محمد القرآن في المسيد؟','2025-10-14 11:15:02.000000',2),(36,'b','2025-10-14 11:15:17.000000','','','[{\"id\": \"a\", \"text\": \"La joie et l\'enthousiasme\", \"textArabic\": \"الفرح والحماس\"}, {\"id\": \"b\", \"text\": \"L\'ennui et la peur\", \"textArabic\": \"الملل والخوف\"}, {\"id\": \"c\", \"text\": \"La curiosité intellectuelle\", \"textArabic\": \"الفضول الفكري\"}, {\"id\": \"d\", \"text\": \"L\'indifférence totale\", \"textArabic\": \"اللامبالاة التامة\"}]','Quelle émotion domine chez l\'enfant narrateur durant ses journées au Msid ?','ما هي المشاعر المهيمنة عند الطفل الراوي خلال أيامه في المسيد؟','2025-10-14 11:15:17.000000',2),(37,'b','2025-10-14 11:15:32.000000','','','[{\"id\": \"a\", \"text\": \"Sa boîte à merveilles\", \"textArabic\": \"صندوق العجائب الخاص به\"}, {\"id\": \"b\", \"text\": \"Sa planchette coranique (لوحة)\", \"textArabic\": \"لوحته القرآنية\"}, {\"id\": \"c\", \"text\": \"Un jouet en bois\", \"textArabic\": \"لعبة خشبية\"}, {\"id\": \"d\", \"text\": \"Un livre d\'histoires\", \"textArabic\": \"كتاب قصص\"}]','Quel objet personnel Sidi Mohammed apporte-t-il souvent avec lui ?','ما هو الشيء الشخصي الذي يصطحبه سيدي محمد معه غالباً؟','2025-10-14 11:15:32.000000',2),(38,'b','2025-10-14 11:15:48.000000','','','[{\"id\": \"a\", \"text\": \"Chaleureuse et accueillante\", \"textArabic\": \"دافئة ومرحبة\"}, {\"id\": \"b\", \"text\": \"Austère et intimidante\", \"textArabic\": \"صارمة ومخيفة\"}, {\"id\": \"c\", \"text\": \"Moderne et innovante\", \"textArabic\": \"حديثة ومبتكرة\"}, {\"id\": \"d\", \"text\": \"Joyeuse et ludique\", \"textArabic\": \"مرحة ومسلية\"}]','Comment est décrite l\'atmosphère générale du Msid dans ce chapitre ?','كيف توصف الأجواء العامة للمسيد في هذا الفصل؟','2025-10-14 11:15:48.000000',2);
/*!40000 ALTER TABLE `qcm_questions` ENABLE KEYS */;
UNLOCK TABLES;

--
-- Table structure for table `questions`
--

DROP TABLE IF EXISTS `questions`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `questions` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `exam_id` bigint NOT NULL,
  `type` varchar(255) COLLATE utf8mb4_general_ci NOT NULL,
  `question` text COLLATE utf8mb4_general_ci NOT NULL,
  `question_arabic` text COLLATE utf8mb4_general_ci,
  `instruction` text COLLATE utf8mb4_general_ci,
  `instruction_arabic` text COLLATE utf8mb4_general_ci,
  `points` int NOT NULL,
  `question_order` int DEFAULT NULL,
  `options` json DEFAULT NULL,
  `sub_questions` json DEFAULT NULL,
  `matching_pairs` json DEFAULT NULL,
  `table_content` json DEFAULT NULL,
  `drag_drop_words` json DEFAULT NULL,
  `progressive_phrases` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `criteria` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `helper` json DEFAULT NULL,
  `answer` text COLLATE utf8mb4_general_ci,
  `answer_arabic` text COLLATE utf8mb4_general_ci,
  `sub_title` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `sub_title_arabic` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `prompt` text COLLATE utf8mb4_general_ci,
  `prompt_arabic` text COLLATE utf8mb4_general_ci,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_exam_id` (`exam_id`),
  KEY `idx_exam_order` (`exam_id`,`question_order`),
  KEY `idx_type` (`type`),
  KEY `idx_created_at` (`created_at`),
  CONSTRAINT `questions_ibfk_1` FOREIGN KEY (`exam_id`) REFERENCES `exams` (`id`) ON DELETE CASCADE,
  CONSTRAINT `questions_chk_1` CHECK (json_valid(`options`)),
  CONSTRAINT `questions_chk_2` CHECK (json_valid(`sub_questions`)),
  CONSTRAINT `questions_chk_3` CHECK (json_valid(`matching_pairs`)),
  CONSTRAINT `questions_chk_4` CHECK (json_valid(`table_content`)),
  CONSTRAINT `questions_chk_5` CHECK (json_valid(`drag_drop_words`)),
  CONSTRAINT `questions_chk_6` CHECK (json_valid(`progressive_phrases`)),
  CONSTRAINT `questions_chk_7` CHECK (json_valid(`criteria`)),
  CONSTRAINT `questions_chk_8` CHECK (json_valid(`helper`))
) ENGINE=InnoDB AUTO_INCREMENT=167 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
